
public class NestedForPyramidNoInc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		{
			int k=0;
		for(int i=3;i>=0;i--) {
			
			
			for(int j=1;j<=4-i;j++)
				
			{
				System.out.print(k+j );
				System.out.print("\t");
					
			}

			System.out.println("");
			
		}
		}}


	}

